import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProductDetails = () => {
  const { id } = useParams(); 
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // Hook pour gérer la navigation

  useEffect(() => {
    axios
      .get(`http://127.0.0.1:8000/api/products/${id}/`) 
      .then((response) => {
        setProduct(response.data.product); 
        setLoading(false);
      })
      .catch((err) => {
        console.error('Erreur:', err);
        setError('Impossible de charger les détails du produit.');
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Chargement...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: '20px', background: 'black', color: '#39ff14' }}>
      <h1 style={{ color: '#ffff00' }}>{product.name}</h1>
      {product.image && (
        <img
          src={`http://127.0.0.1:8000${product.image}`}
          alt={product.name}
          style={{
            width: '300px',
            marginBottom: '20px',
            borderRadius: '10px',
            objectFit: 'cover',
          }}
        />
      )}
      <p>{product.description}</p>
      <p>Prix : <strong>{product.price} €</strong></p>
      <p>Catégorie : <strong>{product.category}</strong></p>

      {/* Bouton de retour */}
      <button
        onClick={() => navigate('/products')}
        style={{
          backgroundColor: '#a020f0',
          color: 'white',
          border: 'none',
          padding: '10px 20px',
          cursor: 'pointer',
          marginTop: '20px',
          borderRadius: '5px',
        }}
      >
        Retour au catalogue
      </button>
    </div>
  );
};

export default ProductDetails;
