import React from 'react';
import { Link } from 'react-router-dom';
import ProductList from '../components/ProductList';

const Products = () => {
  return (
    <div style={{ padding: '20px', background: 'black', color: '#39ff14' }}>
      <h1 style={{ color: '#ffff00' }}>Catalogue de Produits</h1>

      {/* Bouton pour ajouter un produit */}
      <Link to="/add-product">
        <button
          style={{
            padding: '10px 20px',
            backgroundColor: '#39ff14',
            color: 'black',
            border: 'none',
            fontSize: '16px',
            cursor: 'pointer',
            borderRadius: '5px',
            marginBottom: '20px',
          }}
        >
          Ajouter un produit
        </button>
      </Link>

      {/* Bouton pour supprimer tous les produits (si besoin) */}
      <button
        onClick={() => alert('Suppression de tous les produits (à implémenter)')}
        style={{
          padding: '10px 20px',
          backgroundColor: '#ff4136',
          color: 'white',
          border: 'none',
          fontSize: '16px',
          cursor: 'pointer',
          borderRadius: '5px',
          marginBottom: '20px',
        }}
      >
        Supprimer tous les produits
      </button>

      {/* Liste des produits */}
      <ProductList />
    </div>
  );
};

export default Products;
