import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const UpdateProduct = () => {
  const { id } = useParams();  // Récupérer l'ID du produit à mettre à jour
  const [product, setProduct] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    image: null,
  });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Charger les informations du produit à partir de l'API
    axios
      .get(`http://127.0.0.1:8000/api/products/${id}/`)
      .then((response) => {
        setProduct({
          name: response.data.product.name,
          description: response.data.product.description,
          price: response.data.product.price,
          category: response.data.product.category,
          image: response.data.product.image,
        });
      })
      .catch((error) => {
        console.error('Erreur:', error);
        setError('Impossible de charger les informations du produit.');
      });
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', product.name);
    formData.append('description', product.description);
    formData.append('price', product.price);
    formData.append('category', product.category);
    if (product.image) {
      formData.append('image', product.image);
    }

    // Envoi de la requête PUT pour mettre à jour le produit
    axios
      .put(`http://127.0.0.1:8000/api/products/${id}/update/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      })
      .then(() => {
        navigate('/products');  // Rediriger vers la liste des produits après la mise à jour
      })
      .catch((error) => {
        console.error('Erreur lors de la mise à jour du produit:', error);
        setError('Impossible de mettre à jour le produit.');
      });
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Mettre à jour un produit</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          placeholder="Nom du produit"
          value={product.name}
          onChange={(e) => setProduct({ ...product, name: e.target.value })}
          style={styles.input}
          required
        />
        <textarea
          placeholder="Description"
          value={product.description}
          onChange={(e) => setProduct({ ...product, description: e.target.value })}
          style={styles.textarea}
          required
        />
        <input
          type="number"
          placeholder="Prix"
          value={product.price}
          onChange={(e) => setProduct({ ...product, price: e.target.value })}
          style={styles.input}
          required
        />
        <select
          value={product.category}
          onChange={(e) => setProduct({ ...product, category: e.target.value })}
          style={styles.input}
          required
        >
          <option value="">Sélectionner une catégorie</option>
          <option value="Electronics">Électronique</option>
          <option value="Laptops">Laptops</option>
          <option value="Arts">Arts</option>
          <option value="Food">Alimentation</option>
          <option value="Home">Maison</option>
          <option value="Kitchen">Cuisine</option>
        </select>
        <input
          type="file"
          onChange={(e) => setProduct({ ...product, image: e.target.files[0] })}
          style={styles.input}
        />
        <button type="submit" style={styles.submitButton}>
          Mettre à jour le produit
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    backgroundColor: '#0a0a0a',
    color: '#39ff14',
    textAlign: 'center',
    fontFamily: '"Arial", sans-serif',
    borderRadius: '10px',
    boxShadow: '0 0 20px rgba(0, 255, 0, 0.3)',
    maxWidth: '600px',
    margin: '50px auto',
  },
  title: {
    color: '#ffff00',
    fontSize: '2.5rem',
    textShadow: '0 0 10px #ffff00',
    marginBottom: '20px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px',
    alignItems: 'center',
  },
  input: {
    padding: '10px',
    border: '2px solid #39ff14',
    borderRadius: '10px',
    backgroundColor: '#1a1a1a',
    color: '#39ff14',
    fontSize: '16px',
    width: '80%',
  },
  textarea: {
    padding: '10px',
    border: '2px solid #39ff14',
    borderRadius: '10px',
    backgroundColor: '#1a1a1a',
    color: '#39ff14',
    fontSize: '16px',
    width: '80%',
    height: '100px',
  },
  submitButton: {
    backgroundColor: '#39ff14',
    color: 'black',
    border: 'none',
    padding: '15px 30px',
    fontSize: '18px',
    cursor: 'pointer',
    borderRadius: '10px',
    transition: 'background-color 0.3s',
    boxShadow: '0 4px 6px rgba(0, 255, 0, 0.5)',
  },
};

export default UpdateProduct;
