const ProductDetails = () => {
  const { id } = useParams(); 
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`http://127.0.0.1:8000/api/products/${id}/`) 
      .then((response) => {
        setProduct(response.data.product); 
        setLoading(false);
      })
      .catch((err) => {
        console.error('Erreur:', err);
        setError('Impossible de charger les détails du produit.');
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Chargement...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{
      padding: '20px', 
      background: '#0a0a0a', // Fond noir global
      color: '#39ff14', 
      fontFamily: 'Arial, sans-serif',
      textAlign: 'center',
      borderRadius: '10px',
      boxShadow: '0 0 20px rgba(0, 255, 0, 0.3)',
      maxWidth: '800px',
      margin: '50px auto'
    }}>
      <h1 style={{ color: '#ffff00', fontSize: '2.5rem', textShadow: '0 0 10px #ffff00' }}>{product.name}</h1>
      {product.image && (
        <img
          src={`http://127.0.0.1:8000${product.image}`}
          alt={product.name}
          style={{
            width: '100%',
            maxWidth: '500px',
            marginBottom: '20px',
            borderRadius: '10px',
            boxShadow: '0 4px 6px rgba(0, 255, 0, 0.3)',
            objectFit: 'cover',
          }}
        />
      )}
      <p>{product.description}</p>
      <p style={{ color: '#39ff14', fontSize: '1.3rem' }}>Prix : <strong>{product.price} €</strong></p>
      <p style={{ color: '#39ff14', fontSize: '1.1rem' }}>Catégorie : <strong>{product.category}</strong></p>

      {/* Bouton de retour */}
      <button
        onClick={() => navigate('/products')}
        style={{
          backgroundColor: '#a020f0',
          color: 'white',
          border: 'none',
          padding: '15px 30px',
          fontSize: '18px',
          cursor: 'pointer',
          borderRadius: '10px',
          transition: 'background-color 0.3s, transform 0.3s',
          boxShadow: '0 4px 6px rgba(160, 32, 240, 0.6)',
          marginTop: '20px',
        }}
      >
        Retour au catalogue
      </button>
    </div>
  );
};
