import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    keyword: '',
    min_price: '',
    max_price: '',
  });

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value,
    });
  };

  const fetchProducts = () => {
    const query = new URLSearchParams(filters).toString();
    axios
      .get(`http://127.0.0.1:8000/api/products/?${query}`)
      .then((response) => {
        setProducts(response.data.products);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Erreur:', error);
        setError('Impossible de charger les produits.');
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchProducts();
  }, [filters]);

  if (loading) return <p>Chargement des produits...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: '20px', background: 'black', color: '#39ff14' }}>
      <h1 style={{ color: '#ffff00' }}>Catalogue de Produits</h1>

      {/* Formulaire de filtres */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          name="keyword"
          placeholder="Rechercher par mot-clÃ©"
          value={filters.keyword}
          onChange={handleFilterChange}
          style={{
            marginRight: '10px',
            padding: '5px',
            border: '1px solid #a020f0',
            borderRadius: '5px',
          }}
        />
        <input
          type="number"
          name="min_price"
          placeholder="Prix min"
          value={filters.min_price}
          onChange={handleFilterChange}
          style={{
            marginRight: '10px',
            padding: '5px',
            border: '1px solid #a020f0',
            borderRadius: '5px',
          }}
        />
        <input
          type="number"
          name="max_price"
          placeholder="Prix max"
          value={filters.max_price}
          onChange={handleFilterChange}
          style={{
            marginRight: '10px',
            padding: '5px',
            border: '1px solid #a020f0',
            borderRadius: '5px',
          }}
        />
        <button
          onClick={fetchProducts}
          style={{
            padding: '5px 10px',
            backgroundColor: '#39ff14',
            border: 'none',
            borderRadius: '5px',
            color: 'black',
            cursor: 'pointer',
          }}
        >
          Filtrer
        </button>
      </div>

      {/* Liste des produits */}
      {products.length === 0 ? (
        <p>Aucun produit disponible.</p>
      ) : (
        <ul>
          {products.map((product) => (
            <li
              key={product.id}
              style={{ borderBottom: '1px solid #a020f0', marginBottom: '20px' }}
            >
              <h2>{product.name}</h2>
              {product.image && (
                <img
                  src={`http://127.0.0.1:8000${product.image}`}
                  alt={product.name}
                  style={{ width: '150px', marginBottom: '10px' }}
                />
              )}
              <p>{product.description}</p>
              <p>Prix : {product.price} â¬</p>
              <Link
                to={`/product/${product.id}`}
                style={{ color: '#39ff14', textDecoration: 'none' }}
              >
                Voir les dÃ©tails
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ProductList;
