import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // États pour les filtres
  const [keyword, setKeyword] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [category, setCategory] = useState('');
  const [brand, setBrand] = useState('');

  const fetchProducts = () => {
    setLoading(true);
    setError(null);

    // Construire l'URL avec les paramètres de filtre
    const params = new URLSearchParams({
      keyword,
      min_price: minPrice,
      max_price: maxPrice,
      category,
      brand,
    }).toString();

    axios
      .get(`http://127.0.0.1:8000/api/products/?${params}`)
      .then((response) => {
        setProducts(response.data.products);
        setLoading(false);
      })
      .catch((err) => {
        setError('Impossible de charger les produits.');
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchProducts();
  }, []); // Charger les produits au démarrage

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    fetchProducts();
  };

  if (loading) return <p>Chargement des produits...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: '20px', background: 'black', color: '#39ff14' }}>
      <h1 style={{ color: '#ffff00' }}>Catalogue de Produits</h1>

      {/* Formulaire des filtres */}
      <form onSubmit={handleFilterSubmit} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Mot-clé"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <input
          type="number"
          placeholder="Prix minimum"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <input
          type="number"
          placeholder="Prix maximum"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Catégorie"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Marque"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <button type="submit" style={{ background: '#39ff14', color: 'black' }}>
          Filtrer
        </button>
      </form>

      {/* Liste des produits */}
      {products.length === 0 ? (
        <p>Aucun produit disponible.</p>
      ) : (
        <ul>
          {products.map((product) => (
            <li key={product.id} style={{ borderBottom: '1px solid #a020f0', marginBottom: '10px' }}>
              <h2>{product.name}</h2>
              <p>{product.description}</p>
              <p>Prix : {product.price} €</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ProductList;
