import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div style={{ padding: '20px', background: 'black', color: '#39ff14' }}>
      <h1 style={{ color: '#ffff00' }}>Bienvenue sur notre catalogue de produits !</h1>
      <p style={{ color: '#39ff14' }}>
        Découvrez nos produits et profitez des meilleures offres.
      </p>
      {/* Ajout du lien vers la page catalogue */}
      <Link to="/products">
        <button 
          style={{
            backgroundColor: '#39ff14', 
            color: 'black', 
            padding: '10px 20px', 
            fontSize: '16px',
            border: 'none',
            cursor: 'pointer',
            borderRadius: '5px',
          }}
        >
          Accéder au catalogue
        </button>
      </Link>
    </div>
  );
};

export default Home;
